import react from "react";
import {View, Text, StyleSheet } from 'react-native';

const Tasks = (props) => {
   
    return (
        <View style={styles.item}>
        <View style={styles.itemLeft}>
            <View style={styles.square}></View>
            <Text style={styles.itemsText}>{props.text}</Text>
        </View>
        <View style={styles.circular}></View>
     </View>
    )
}


const styles = StyleSheet.create({
    item: {
        backgroundColor: '#FFF',
        padding: 15,
        borderRadius: 10,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent:'space-between',
        marginBottom: 20,
    },
    itemLeft: {
        flexDirection: 'row',
        alignItems: 'center',
        flexWrap: 'wrap',

    },
    square: {
        width: 24, 
        height: 24,
        backgroundColor: '#55BCF6',
        opacity: 0.4,
        borderRadius: 5,
        marginRight: 15,
    },
    itemsText: {
        maxWidth: '80%',
    },
    circular: {
        width: 12,
        height: 12,
        borderBottomColor: '#55BCF6',
        borderBottomWidth: 2,
        borderRadius: 5,
    },  

});

export default Tasks;
